import React, { useState, useEffect } from "react";
import BottomNav from "../components/BottomNav";
import { publicApi } from '../components/Api';

export default function Leaderboard() {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
  
    useEffect(() => {
    const loadUser = async () => {
      setLoading(true);
        try{
        const res = await publicApi.get("/health");
      } catch (err) {
        console.error("‚ùå Error loading user:", err.message);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadUser();
  }, []);
    
    const topThree = [
        { id: 1, name: "Alice", avatar_url: "data:image/jpeg;base64,", point: 1500, rank: 1 },
        { id: 2, name: "Bob", avatar_url: "data:image/jpeg;base64,", point: 1200, rank: 2 },
        { id: 3, name: "Charlie", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTnWy0U2VzSxr-oYUV4owrHonD1_XWhLicozA&s", point: 1000, rank: 3 },
    ];

    const others = [
        { id: 4, name: "David", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTDPV0CONbBVZND7Ynjb4w8S_ygXP25nLHTBw&s", point: 900, rank: 4 },
        { id: 5, name: "Eve", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTe9inEEs_PrHjk0vHkhOMHshkeu--si2lIwg&s", point: 800, rank: 5 },
        { id: 6, name: "Frank", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTmu8DV50poDRu9k0e_Hvwrg3ahd-7LiUI-ew&s", point: 700, rank: 6 },
        { id: 7, name: "Grace", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRhobBjmDApQRHhgXkPU1QcTDS82Dfib301jg&s", point: 650, rank: 7 },
        { id: 8, name: "Hannah", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQpeElblRKBbK40BYHXOjuaMnID0uzDVefpA&s", point: 600, rank: 8 },
        { id: 9, name: "Ian", avatar_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMjBA2tQfTLqBH0ysWTvRLQX6uDtagGJYHtw&s", point: 550, rank: 9 },
        { id: 10, name: "Jack", avatar_url: "data:image/jpeg;base64,", point: 500, rank: 10 },
    ];

    const currentUser = { id: 11, name: "You", avatar_url: "data:image/jpeg;base64,", point: 480, rank: 12 };

if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#000000] text-white font-poppins">
        <p>Loading...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#000000] text-white font-poppins">
        <p>Error loading user data: {error}</p>
      </div>
    );
  }

    return (
        <div className="min-h-screen pb-28 w-full bg-[#000000] text-white font-sans">

            <main className="px-4 py-10 flex flex-col items-center gap-10">
                {/* Page Header */}
                <header className="w-full max-w-3xl text-center">
                    <h1 className="text-3xl sm:text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-[#A259FF] to-[#B388FF] drop-shadow-lg">
                        Leaderboard
                    </h1>
                    <p className="text-gray-400 mt-2 text-sm sm:text-base">
                        Track top referrers and see how you rank! Invite friends and climb the leaderboard.
                    </p>
                </header>

                {/* Top 3 Users */}
                <section className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 w-full max-w-6xl">
                    {topThree.map((user) => (
                        <div
                            key={user.id}
                            className={`flex flex-col items-center justify-center rounded-xl p-6 bg-[#1a1a2e]`}
                        >
                            <div className="w-20 h-20 rounded-full overflow-hidden border-4 border-[#5B2EFF] mb-3">
                                <img
                                    src={user.avatar_url}
                                    alt={user.name}
                                    className="w-full h-full object-cover"
                                />
                            </div>
                            <div className="font-bold text-lg sm:text-xl truncate">{user.name}</div>
                            <div className="text-xl sm:text-2xl font-bold text-purple-400 mt-1">{user.point.toLocaleString()} pts</div>
                            <div className="text-gray-300 text-sm mt-1">
                                {user.rank === 1 ? "1st" : user.rank === 2 ? "2nd" : "3rd"}
                            </div>
                        </div>
                    ))}
                </section>

                {/* Other Users */}
                <section className="bg-[#1a1a2e] w-full max-w-6xl rounded-xl py-4 divide-y divide-[#292947] px-2">
                    {others.map((user) => (
                        <div
                            key={user.id}
                            className={`flex justify-between items-center px-2 sm:px-6 py-4 rounded-lg ${user.id === currentUser.id ? "ring-2 ring-[#A259FF]" : ""
                                }`}
                        >
                            <div className="flex items-center gap-3 sm:gap-4 overflow-hidden">
                                <span className="text-gray-400 w-5 sm:w-6 text-right">{user.rank}</span>
                                <img
                                    src={user.avatar_url}
                                    alt={user.name}
                                    className="w-9 h-9 sm:w-10 sm:h-10 rounded-full"
                                />
                                <span className="font-semibold truncate">{user.name}</span>
                            </div>
                            <div className="text-gray-300 font-semibold">{user.point.toLocaleString()} pts</div>
                        </div>
                    ))}
                </section>

                {/* Current User */}
                {currentUser && (
                    <section className="bg-[#1a1a2e] w-full max-w-6xl rounded-xl px-4 sm:px-6 py-6 relative">
                        <div className="absolute -top-3 left-6 bg-purple-600 text-white text-xs px-3 py-1 rounded-full font-semibold shadow-md">
                            Your Rank
                        </div>
                        <div className="flex justify-between items-center text-sm sm:text-base flex-wrap gap-y-4">
                            <div className="flex items-center gap-3 sm:gap-4">
                                <span className="bg-purple-700 text-purple-300 text-xs px-3 py-1 rounded-full font-bold">
                                    #{currentUser.rank}
                                </span>
                                <img
                                    src={currentUser.avatar_url}
                                    alt={currentUser.name}
                                    className="w-10 h-10 sm:w-11 sm:h-11 rounded-full"
                                />
                                <div>
                                    <div className="text-white font-semibold truncate">{currentUser.name}</div>
                                </div>
                            </div>
                            <div className="text-purple-400 font-bold text-base sm:text-lg text-right">
                                {currentUser.point?.toLocaleString()} pts
                            </div>
                        </div>
                    </section>
                )}
            </main>

            <BottomNav />
        </div>
    );
}